#include <vector>

using namespace std;

mutex mut;

void pi_digits(int start, int end, vector<int>& digits) {
    int carry = 0;
    for (int i = end; i >= start; i--) {
        int sum = 0;
        for (int j = i; j > 0; j--) {
            sum = sum * j + digits[j] * 10000;
            digits[j] = sum % (j * 2 - 1);
            sum /= j * 2 - 1;
        }
        carry = (carry + digits[1] / 10000);
        digits[1] %= 10000;
    }
    mut.lock();
    cout << carry;
    for (int i = 2; i < digits.size(); i++) {
        printf("%04d", digits[i] / 10000);
        digits[i] = digits[i] % 10000;
    }
    mut.unlock();
}

int main() {
    const int num_threads = 4;
    vector<int> digits(100);
    vector<thread> threads(num_threads);

    for (int i = 0; i < num_threads; i++) {
        int start = i * (digits.size() - 1) / num_threads + 1;
        int end = (i + 1) * (digits.size() - 1) / num_threads;
        threads[i] = thread(pi_digits, start, end, ref(digits));
    }

